import React, { useRef, useEffect, useState } from 'react';

const Contact = () => {
  const welcomeDivRef = useRef(null);

  const applyFloatInAnimation = (divRef) => {
    const observer = new IntersectionObserver((entries) => {
      const entry = entries[0];
      if (entry.isIntersecting) {
        divRef.current.classList.add('float-in');
      }
    });

    observer.observe(divRef.current);

    return () => {
      observer.disconnect();
    };
  };

  useEffect(() => {
    applyFloatInAnimation(welcomeDivRef);

  }, []);

  const [Name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', {
      Name,
      email,
      message,
    });
    setName('');
    setEmail('');
    setMessage('');
    setIsSubmitted(true);
  };

  return (
    <div  className="contact"  style={{position:"relative"}}>

      <div id="contact"></div>
      <h1>0.3 Say hi</h1>
      <h2>Let's Chat</h2>
      <a href="mailto:jordanakasaato@gmail.com">Send Email</a>



      <div ref={welcomeDivRef} className="contact-container floating-div">

        {isSubmitted ? (
          <div className="sent-message">
            <h4>Message Sent! Thanks for reaching out!</h4>
          </div>
        ) : (
          <form onSubmit={handleSubmit}>
            <div>
              <label className="contact-label"> Name:</label>
              <input
                type="text"
                value={Name}
                onChange={(e) => setName(e.target.value)}
                required
                className="contact-input"
              />
            </div>
            <div>
              <label className="contact-label">Email:</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                className="contact-input"
              />
            </div>
            <div>
              <label className="contact-label">Message:</label>
              <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                required
                className="contact-textarea"
              />
            </div>
            <button type="submit" className="contact-button">
              Submit
            </button>

          </form>
        )}

      </div>
    </div>
  );
};

export default Contact;
